        'ボ': 'ホ',
        'ガ': 'カ',
        'ギ': 'キ',
        'グ': 'ク',
        'ゲ': 'ケ',
        'ゴ': 'コ',
        'ザ': 'サ',
        'ジ': 'シ',
        'ズ': 'ス',
        'ゼ': 'セ',
        'ゾ': 'ソ',
